targets.expr:
(
  kubelet_volume_stats_capacity_bytes{job="kubelet", metrics_path="/metrics", persistentvolumeclaim=~"minecraft.+"}
  -
  kubelet_volume_stats_available_bytes{job="kubelet", metrics_path="/metrics", persistentvolumeclaim=~"minecraft.+"}
)
/
kubelet_volume_stats_capacity_bytes{job="kubelet", metrics_path="/metrics", persistentvolumeclaim=~"minecraft.+"}
* 100

sum(
  node_namespace_pod_container:container_cpu_usage_seconds_total:sum_rate
* on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{workload_type="deployment", workload=~"$container"}
) by (workload, workload_type)
